import React from "react";

const LessonItem = ({ lesson }) => (
  <li className="nav-item">
    <a className="nav-link active" href={`/courses/123/modules/${lesson.moduleId}/lessons/${lesson.id}/topics/1`}>
    {/* /courses/${module.courseId}/modules/${module.id}/lessons/1/topics/1 */}
      {lesson.title}
    </a>
  </li>
)
            // path="/courses/:courseId/modules/:moduleId/lessons/:lessonId/topics/:topicId"

export default LessonItem;
